UPDATE `db_script_string` SET `content_loc3`='So, es ist vollbracht.' WHERE `entry`=2000000444;
UPDATE `db_script_string` SET `content_loc3`='Samia, wisst Ihr wer Kolphis Dunkelschuppe ist?' WHERE `entry`=2000000445;
