UPDATE `db_script_string` SET `content_loc3`='Danke, ich hätte den Silberwald niemals verlassen sollen.' WHERE `entry`=2000000453;
UPDATE `db_script_string` SET `content_loc3`='Da... Dank Euch. Ich war mir sicher, dass ich sterben werde.' WHERE `entry`=2000000454;
UPDATE `db_script_string` SET `content_loc3`='Vielen Dank, dass Ihr alle heute Abend gekommen seid. Und nun klatscht in die Hände für Shattraths Liebling, Hel\'gesh Neider!' WHERE `entry`=2000000456;
