UPDATE `db_script_string` SET `content_loc3`='Wenn Ihr bereit für einen Lacher seid, geht in die Taverne Weltenend!' WHERE `entry`=2000000459;
UPDATE `db_script_string` SET `content_loc3`='Jetzt in Shattrath: die Tauren Chieftains! Sie spielen zur vollen Stunde in der Taverne Weltenend.' WHERE `entry`=2000000460;
