UPDATE `db_script_string` SET `content_loc3`='Ich bin frei? Ich bin frei!' WHERE `entry`=2000000463;
UPDATE `db_script_string` SET `content_loc3`='Danke, $R. Ich bin sicher, mein Vater wird Euch reich belohnen! Tschüss!' WHERE `entry`=2000000464;
UPDATE `db_script_string` SET `content_loc3`='Das ist das letzte Mal, dass ich mich fangen lasse! Versprochen! Tschüss!' WHERE `entry`=2000000465;
