UPDATE `db_script_string` SET `content_loc3`='Was erwarten sie denn, was passiert, wenn sie die Fledermäuse zwingen, in so einem Winkel hereinzukommen? Genickbrüche und schlecht schmeckenden Fledermauseintopf, das ist, was sie kriegen!' WHERE `entry`=2000000467;
UPDATE `db_script_string` SET `content_loc3`='Aaarrrrr!!' WHERE `entry`=2000000470;
