UPDATE `db_script_string` SET `content_loc3`='Euer Körper gehört mir! Unsere Schicksale sind für immer verflochten!' WHERE `entry`=2000000472;
UPDATE `db_script_string` SET `content_loc3`='Vorsicht, $C. Diese Trolle haben ihren eigenen Schlangengott getötet!' WHERE `entry`=2000000473;
