UPDATE `locales_page_text` SET `text_loc11`='Benvenuto in World of Warcraft!$B$BWe Mage\'s è il più potente utente di Magic. Combiniamo Fire and Frost per ottenere il massimo effetto e ti aiuteremo a trovare ciò di cui hai bisogno Andate e imparate la magia di esplorare!$B$' WHERE `entry`=2602;
