UPDATE `locales_page_text` SET `text_loc11`='<HTML><BODY><BR/><P> Versione liberamente tradotta ... </P><BR/><P> Alla luce, alla luce del sole, </P><P> Figli di il sangue, </P><P> I nostri nemici stanno sfondando. </P><P> I bambini del sangue, </P><P> Alla luce, </P><P> I bambini che non hanno sangue, </P><P> Stanno sfondando. </P><P> O figli del sangue, </P><P> Alla luce del sole. </P><P> Bambini in difetto </P><P> O figli del sangue, </P><P> Alla luce del sole, </P><P> Il sole. </P></BODY></HTML>' WHERE `entry`=2991;
