UPDATE `locales_npc_text` SET `Text0_0_loc8`='Да хранит тебя Кенарий, $Gбрат:сестра;.$B$BЯ – Таджарри. Я поклялась защищать это святилище. Для меня большая честь служить хранителю Ремулу на этой священной земле.',`Text0_1_loc8`='Да хранит тебя Кенарий, $Gбрат:сестра;.$B$BЯ – Таджарри. Я поклялась защищать это святилище. Для меня большая честь служить хранителю Ремулу на этой священной земле.' WHERE `entry`=4978;
