UPDATE `locales_npc_text` SET `Text0_0_loc8`='Ты $Gдостиг:достигла; значительных успехов. Продолжим обучение!',`Text0_1_loc8`='Ты $Gдостиг:достигла; значительных успехов. Продолжим обучение!' WHERE `entry`=5247;
