UPDATE `locales_npc_text` SET `Text0_0_loc8`='Покинь это место, $Gнезнакомец:незнакомка;, пока оно не поглотило тебя, как все, к чему оно прикасается.',`Text0_1_loc8`='Покинь это место, $Gнезнакомец:незнакомка;, пока оно не поглотило тебя, как все, к чему оно прикасается.' WHERE `entry`=6896;
