UPDATE `locales_npc_text` SET `Text0_0_loc8`='Добро пожаловать в деревню Сломанного Клыка, $Gстранник:странница;. Я – Старейшина племени Сломанного Клыка, предводительница местных троллей.',`Text0_1_loc8`='Добро пожаловать в деревню Сломанного Клыка, $Gстранник:странница;. Я – Старейшина племени Сломанного Клыка, предводительница местных троллей.' WHERE `entry`=7238;
