UPDATE `locales_npc_text` SET `Text0_0_loc8`='Я могу научить тебя кузнечному делу. Только попроси вежливо.',`Text0_1_loc8`='Я могу научить тебя кузнечному делу. Только попроси вежливо.' WHERE `entry`=7246;
