UPDATE `locales_npc_text` SET `Text0_0_loc8`='Полеты из Гром\'гола в Подгород красивы, хоть и проходят без особых происшествий. Но это не повод спать на работе.',`Text0_1_loc8`='Полеты из Гром\'гола в Подгород красивы, хоть и проходят без особых происшествий. Но это не повод спать на работе.' WHERE `entry`=12333;
