UPDATE `locales_npc_text` SET `Text2_0_loc8`='Не смотри на меня, |3-6($R). Я опозорил Орду. Со мной все кончено.',`Text3_0_loc8`='Ни один орк не должен бежать с поля боя, как это сделал я. Оставьте меня наедине с моим позором. О если бы я умер в этой башне! Но я боюсь узнать, что подумают мои потомки о моей трусости.' WHERE `entry`=15117;
