UPDATE `locales_npc_text` SET `Text0_0_loc8`='Эй, $Gпарень:девица;, мне тоже принадлежит кусочек этого пирога.',`Text0_1_loc8`='Эй, $Gпарень:девица;, мне тоже принадлежит кусочек этого пирога.',`Text1_0_loc8`='Детка, это тебе нужно? Ну так забирай!',`Text1_1_loc8`='Детка, это тебе нужно? Ну так забирай!' WHERE `entry`=15501;
UPDATE `locales_npc_text` SET `Text2_0_loc8`='Не мешкай, |3-6($C)! Собирай все, что можешь!',`Text2_1_loc8`='Не мешкай, |3-6($C)! Собирай все, что можешь!',`Text3_0_loc8`='Какое тело таурена ни обыщешь – все один бисер и бесполезный мусор. Удалось найти что-нибудь ценное?',`Text3_1_loc8`='Какое тело таурена ни обыщешь – все один бисер и бесполезный мусор. Удалось найти что-нибудь ценное?' WHERE `entry`=15501;
UPDATE `locales_npc_text` SET `Text4_0_loc8`='Эй, |3-6($C)! Похоже, здесь уже многие побывали, но все затраты времени на поиски чего-либо ценного окупаются.',`Text4_1_loc8`='Эй, |3-6($C)! Похоже, здесь уже многие побывали, но все затраты времени на поиски чего-либо ценного окупаются.',`Text5_0_loc8`='Э-э, ты же сюда не арестовывать нас $Gпришел:пришла;, так?',`Text5_1_loc8`='Э-э, ты же сюда не арестовывать нас $Gпришел:пришла;, так?' WHERE `entry`=15501;
