UPDATE `locales_npc_text` SET `Text0_0_loc8`='В джунглях много денег, |3-6($R). Понимаешь?$B$BНет, это не золото и не медь, а шкуры ящеров, минеральные жилы и старинные артефакты троллей.$B$BЛадно, может, среди минеральных жил и попадаются золотые и медные.$B$BНо общую идею ты просекаешь, да?' WHERE `entry`=16340;
