UPDATE `locales_npc_text` SET `Text0_0_loc8`='Добро пожаловать в крепость Стражей Пустоты, |3-6($C). Зови меня "мама". Я о тебе позабочусь.',`Text0_1_loc8`='Добро пожаловать в крепость Стражей Пустоты, |3-6($C). Зови меня "мама". Я о тебе позабочусь.' WHERE `entry`=16574;
UPDATE `locales_npc_text` SET `Text0_0_loc8`='В смерти мы перерождаемся.',`Text0_1_loc8`='В смерти мы перерождаемся.' WHERE `entry`=16575;
