UPDATE `trans_words` SET `content_loc4`='"Class skills teachers can be found on the lower levels in the center of the Terrace of the Light.$B$B Someone can always help you regardless of whether you belong to the Aldor, the Seers or some other faction."' WHERE `word_id` = 280310; -- locales_npc_text - Text0_1 : entry=17895
UPDATE `trans_words` SET `content_loc4`='"Don\'t be offended, baby, but now I\'m on the side of the merchant prince.$B$B Go your way, okay? You stop me from admiring the scenery."' WHERE `word_id` = 279969; -- locales_npc_text - Text0_1 : entry=14954
UPDATE `trans_words` SET `content_loc4`='"Don\'t worry, child. I know that our agreement was ... temporary. Everything will be fine with me.$B$BA And you, you do not belong to this place. Your time has not come yet. I will help you return to the living world.$B$BThis will be mine."' WHERE `word_id` = 279980; -- locales_npc_text - Text0_1 : entry=15097
