UPDATE `trans_words` SET `content_loc4`='"Glad to meet you, sister in arms. I can stall your pets or help you get your fallen comrades back."' WHERE `word_id` = 279589; -- locales_npc_text - Text0_1 : entry=13662
UPDATE `trans_words` SET `content_loc4`='"Glory to you, hero! The echo of the defeat of Yogg-Saron has settled in these walls forever."' WHERE `word_id` = 279574; -- locales_npc_text - Text0_1 : entry=14452
UPDATE `trans_words` SET `content_loc4`='"He is obsessed with gems, but I love him. Let\'s just say I\'m his favorite gem!$B$BWell, what brings you to me? Archeology, right?"' WHERE `word_id` = 280344; -- locales_npc_text - Text0_1 : entry=18074
