UPDATE `trans_words` SET `content_loc4`='Find the representatives of the Reliquary on the Royal Exchange. I have no doubt that among them there will be many archaeologists who will gladly help you.' WHERE `word_id` = 280224; -- locales_npc_text - Text0_1 : entry=17209
