UPDATE `trans_words` SET `content_loc4`='You mean to say there is a warlock? Here, in goodly Theramore?!$B$BOh, my. Someone must inform Lady Proudmoore immediately!$B$B...heh.' WHERE `word_id` = 202811; -- locales_npc_text - Text0_1 : entry=18067
