UPDATE `trans_words` SET `content_loc8`='<Mataus pockets the gold.>$B$BEnjoy the repair costs, $R. Naxxramas is \'unkind\' to the uninitiated.' WHERE `word_id` = 145190; -- locales_quest - offerrewardtext : entry=9211
