UPDATE `trans_words` SET `content_loc8`='Defend Kanati Greycloud' WHERE `word_id` = 171379; -- locales_quest - objectives : entry=4905
UPDATE `trans_words` SET `content_loc8`='Deliana had you contact me? I\'ll do my best to help you, but first I must ask a great favor of you.' WHERE `word_id` = 144593; -- locales_quest - offerrewardtext : entry=8929
UPDATE `trans_words` SET `content_loc8`='Deliver 2 Incendosaur Scales, 4 Kingsblood and one Coal to Master Smith Burninate.' WHERE `word_id` = 142438; -- locales_quest - objectives : entry=7736
