UPDATE `trans_words` SET `content_loc8`='Demons? Oh, there be lots I can tell yeh \'bout demons!' WHERE `word_id` = 174137; -- locales_quest - offerrewardtext : entry=14372
UPDATE `trans_words` SET `content_loc8`='Dere ya have it, $N. It look like we gonna lead an assault on da Gurubashi in Zul\'Gurub.$B$BDese be dire times for us Darkspear. But dey be even more dire for da Zandalar. Dey goin\' too far.$B$BYa been good to Bwemba, hopefully ya gonna continue to help out all of Stranglethorn.$B$BSpirits be wit\' ya, $N.' WHERE `word_id` = 189783; -- locales_quest - offerrewardtext : entry=29100
UPDATE `trans_words` SET `content_loc8`='DESCRIPTION: Complete this quest for Specialization 1.' WHERE `word_id` = 158871; -- locales_quest - details : entry=12179
UPDATE `trans_words` SET `content_loc8`='Dese are in surprisingly good condition - good work, mon.' WHERE `word_id` = 180652; -- locales_quest - offerrewardtext : entry=26313
UPDATE `trans_words` SET `content_loc8`='Dese are some fine pelts, boy. We could make some hefty cloaks outta dese.' WHERE `word_id` = 175153; -- locales_quest - offerrewardtext : entry=24641
