UPDATE `trans_words` SET `content_loc8`='Speak to the Horde Recruiter inside Warsong Hold in Borean Tundra or Vengeance Landing in Howling Fjord.' WHERE `word_id` = 159862; -- locales_quest - objectives : entry=12480
