UPDATE `trans_words` SET `content_loc8`='Speak with Ghabar at the Stormspire.' WHERE `word_id` = 150983; -- locales_quest - objectives : entry=10423
UPDATE `trans_words` SET `content_loc8`='Speak with Gnarl at the Warsong Lumber Camp in Ashenvale.' WHERE `word_id` = 346715; -- locales_quest - CompletedText : entry=26454
UPDATE `trans_words` SET `content_loc8`='Speak with High Priestess Alathea in Darnassus.' WHERE `word_id` = 171394; -- locales_quest - objectives : entry=5682
UPDATE `trans_words` SET `content_loc8`='Speak with Jol in the Exodar.' WHERE `word_id` = 147639; -- locales_quest - objectives : entry=9599
