UPDATE `trans_words` SET `content_loc6`='$N - without heroes like you working on behalf of the Forsaken, we\'d surely suffer. Thank you for your continued efforts!' WHERE `word_id` = 271827; -- locales_quest - offerrewardtext : entry=7819
