UPDATE `trans_words` SET `content_loc6`='...and I thought ogres smelled foul enough to begin with.$B$BWhen you bring me Mug\'Thol\'s head, I will have the four of them mounted on pikes above Alterac. A fitting monument to those who go against the Dark Lady\'s wishes.' WHERE `word_id` = 132364; -- locales_quest - offerrewardtext : entry=519
UPDATE `trans_words` SET `content_loc6`='...and that makes 15 of them. Justice had a name today... and that name was Maximillian of Northshire. For Doloria!' WHERE `word_id` = 175319; -- locales_quest - offerrewardtext : entry=24704
