UPDATE `trans_words` SET `content_loc6`='Ably done, $N. Now we see if my suspicions are correct.' WHERE `word_id` = 178465; -- locales_quest - offerrewardtext : entry=25716
UPDATE `trans_words` SET `content_loc6`='Abominations... strong. $N... stronger.' WHERE `word_id` = 183680; -- locales_quest - offerrewardtext : entry=27160
UPDATE `trans_words` SET `content_loc6`='About bloody time! Pulroy and Zedd are dead.$B$BAnd Daegarn...$B$B<Glorenfeld shakes his head.>$B$BTaken by the Dragonflayer chieftain into the depths of these catacombs. I overheard that oversized mongrel mention something \'bout a sacrifice to the "Dread King."$B$BThe cipher is hidden away in Daegarn\'s grimy beard.$B$BWhat? Dwarves hide things in their beards all the time!' WHERE `word_id` = 156149; -- locales_quest - offerrewardtext : entry=11276
