UPDATE `trans_words` SET `content_loc6`='Above the stale, unwashed stench, you catch a whiff of a familiar smell!  It appears as if this once-mighty adventurer has a real love for kungaloosh.$B$BPeeking through the bars, you can barely make out the recipe written on his arm....' WHERE `word_id` = 163895; -- locales_quest - offerrewardtext : entry=13571
