UPDATE `trans_words` SET `content_loc6`='Alliance killed in Venture Bay' WHERE `word_id` = 159508; -- locales_quest - ObjectiveText1 : entry=12317
UPDATE `trans_words` SET `content_loc6`='Alliance Laborers slain and converted' WHERE `word_id` = 182847; -- locales_quest - ObjectiveText1 : entry=26937
UPDATE `trans_words` SET `content_loc6`='Alliance Lookout restocked' WHERE `word_id` = 178608; -- locales_quest - ObjectiveText1 : entry=25749
