UPDATE `trans_words` SET `content_loc6`='Arngrim\'s spirit fed' WHERE `word_id` = 162325; -- locales_quest - ObjectiveText1 : entry=13046
UPDATE `trans_words` SET `content_loc6`='Arnold and I used to relax down at the moat in the Valley of Heroes. One day, I lost my locket in the lake and I have never been able to find it. Rumor has it that huge royal monkfish live in that moat and that they eat anything!  I know its a long shot, but do you think you could catch a few monkfish and look for my locket?$B$BPlease let me know right away if you find it, it\'s very precious to me.' WHERE `word_id` = 181229; -- locales_quest - details : entry=26488
UPDATE `trans_words` SET `content_loc6`='Aros\'s body appears to be lifeless yet still warm. Perhaps his killers are nearby.' WHERE `word_id` = 172736; -- locales_quest - offerrewardtext : entry=13895
UPDATE `trans_words` SET `content_loc6`='Arr Me Hearty! Gaining one Trinket be not enough to make ye master of the Arena! Now tis the time to truly prove your steel.' WHERE `word_id` = 142502; -- locales_quest - offerrewardtext : entry=7810
UPDATE `trans_words` SET `content_loc6`='Arranging for your flight wasn\'t cheap. Let\'s hope you have what it takes to get the job done.' WHERE `word_id` = 150028; -- locales_quest - offerrewardtext : entry=10194
UPDATE `trans_words` SET `content_loc6`='Arranging the crystals on the table, you carefully align the colors. As you push the last one into place, a burst of light fuses the crystals together.$B$BThe resulting combination is now yours.' WHERE `word_id` = 139088; -- locales_quest - offerrewardtext : entry=24724
