UPDATE `trans_words` SET `content_loc6`='Aye, Hemet\'s here. He\'s at the camp, just go northwest toward the river and you\'ll soon see it. And for the love of the Light, $G lad:lass;, have him send someone to cut me down!' WHERE `word_id` = 160030; -- locales_quest - offerrewardtext : entry=12521
