UPDATE `trans_words` SET `content_loc6`='Enough! Your enthusiasm is impressive, but foolish. Gazing through the veil for too long without practice will break your mind.$B$BThe rest will be mine, $N. For now, I summon you back to me.' WHERE `word_id` = 187560; -- locales_quest - offerrewardtext : entry=28261
