UPDATE `trans_words` SET `content_loc6`='Feels good to get a little blood on your hands, doesn\'t it? Especially when that blood belongs to such self-deluded fools.' WHERE `word_id` = 185278; -- locales_quest - offerrewardtext : entry=27655
UPDATE `trans_words` SET `content_loc6`='Fel Cannonball Stacks destroyed' WHERE `word_id` = 153300; -- locales_quest - ObjectiveText1 : entry=11010
UPDATE `trans_words` SET `content_loc6`='Fel Fires Siphoned' WHERE `word_id` = 172503; -- locales_quest - ObjectiveText1 : entry=13730
UPDATE `trans_words` SET `content_loc6`='Fel Rock grellkin killed with Ireroot Seeds' WHERE `word_id` = 172883; -- locales_quest - ObjectiveText1 : entry=13946
UPDATE `trans_words` SET `content_loc6`='Felendren\'s head... you are to be commended, $N. You\'ve succeeded where others, like Felendren, have utterly failed. Perhaps you are truly ready to be a contributing member of $R society.$B$BYour success here means that you are capable of surviving the greater threats that lurk in Eversong... and believe me, there are plenty to face.' WHERE `word_id` = 145938; -- locales_quest - offerrewardtext : entry=8335
UPDATE `trans_words` SET `content_loc6`='Felnok was right! I do need your help again, and this time... it\'s going to be really dangerous.$B$BAre you ready??' WHERE `word_id` = 139562; -- locales_quest - offerrewardtext : entry=4907
UPDATE `trans_words` SET `content_loc6`='Felt good, didn\'t it? You\'re not one of them anymore, $N. You\'re Scourge. You\'re one of us. Forever...' WHERE `word_id` = 161061; -- locales_quest - offerrewardtext : entry=12739
UPDATE `trans_words` SET `content_loc6`='Female Frost Leopards recovered' WHERE `word_id` = 163884; -- locales_quest - ObjectiveText1 : entry=13549
