UPDATE `trans_words` SET `content_loc6`='Freedom at last! Thank you, $N. Revantusk will know your deeds!' WHERE `word_id` = 142574; -- locales_quest - offerrewardtext : entry=7846
UPDATE `trans_words` SET `content_loc6`='FREEDOM!$B$BDo not be afraid, little one. Gymer is an honorable giant. Like you and your people, I too wish to see the Scourge driven away from this land and the Lich King destroyed. I will help you, as promised, to the best of my ability.' WHERE `word_id` = 161795; -- locales_quest - offerrewardtext : entry=12916
UPDATE `trans_words` SET `content_loc6`='Frenzied Cyclone bracers destroyed' WHERE `word_id` = 172108; -- locales_quest - ObjectiveText1 : entry=13542
UPDATE `trans_words` SET `content_loc6`='Frenzyheart Attacker slain' WHERE `word_id` = 160892; -- locales_quest - ObjectiveText1 : entry=12705
UPDATE `trans_words` SET `content_loc6`='Frenzyheart Attacker' WHERE `word_id` = 161144; -- locales_quest - ObjectiveText1 : entry=12761
UPDATE `trans_words` SET `content_loc6`='Fresh and stinking, these innards will prove to be irresistible for Nemesis, the befouled turtle god created by Twilight\'s Hammer.$B$BNemesis must eventually be destroyed, but first, we must collect a piece of his fiery armored shell. With it, we\'ll be able to work some magic to free the true Ancient.$B$BGo into the Flamewake and drop this bait near Nemesis. It will distract him. Pry off a piece of his shell... then run, if you hope to survive the encounter.' WHERE `word_id` = 177669; -- locales_quest - details : entry=25507
UPDATE `trans_words` SET `content_loc6`='Fresh Dinosaur Meat fed to Venomhide Hatchling' WHERE `word_id` = 164457; -- locales_quest - ObjectiveText1 : entry=13889
UPDATE `trans_words` SET `content_loc6`='Freshly sprouted mushroom destroyed' WHERE `word_id` = 183296; -- locales_quest - ObjectiveText1 : entry=27050
UPDATE `trans_words` SET `content_loc6`='Frightened Miner Rescued' WHERE `word_id` = 178796; -- locales_quest - ObjectiveText1 : entry=25809
