UPDATE `trans_words` SET `content_loc6`='Glorious!  This is the last bit I need. Actually, stick around, $N. Let me tell you a little bit more about our plan.' WHERE `word_id` = 182836; -- locales_quest - offerrewardtext : entry=26934
UPDATE `trans_words` SET `content_loc6`='Glorious! Beautiful stuff, this ore.$B$BGood on you for not droppin\' your pick. Last person I sent out let the butt end hit the floor, and that part\'s not sparkproof.' WHERE `word_id` = 178548; -- locales_quest - offerrewardtext : entry=25735
