UPDATE `trans_words` SET `content_loc6`='Glorious.$B$BNow we will deal a blow to the stone dragons that they will remember for the next hundred years.' WHERE `word_id` = 181875; -- locales_quest - offerrewardtext : entry=26658
UPDATE `trans_words` SET `content_loc6`='Glory to the naaru, $N. You\'ve done the Light\'s bidding by defeating those who follow Illidan.' WHERE `word_id` = 151733; -- locales_quest - offerrewardtext : entry=10619
UPDATE `trans_words` SET `content_loc6`='Glory to the Scryers, $N. You\'ve done the Seer\'s bidding by defeating those who follow Illidan.' WHERE `word_id` = 152424; -- locales_quest - offerrewardtext : entry=10807
UPDATE `trans_words` SET `content_loc6`='Glynda sent you to lend a hand, did she? I\'m glad she did - I could certainly use the help. I\'m working on our initial plans to start whittling away at their ranks. Please feel free to help Tysha and Balthule in the meantime.' WHERE `word_id` = 172268; -- locales_quest - offerrewardtext : entry=13589
