UPDATE `trans_words` SET `content_loc6`='Heal Tavara' WHERE `word_id` = 147589; -- locales_quest - ObjectiveText1 : entry=9586
UPDATE `trans_words` SET `content_loc6`='Heal Wounded Brave' WHERE `word_id` = 183360; -- locales_quest - ObjectiveText1 : entry=27066
UPDATE `trans_words` SET `content_loc6`='Heal Wounded Outrunners' WHERE `word_id` = 169936; -- locales_quest - ObjectiveText1 : entry=10072
UPDATE `trans_words` SET `content_loc6`='Heal Wounded Sentinels' WHERE `word_id` = 182894; -- locales_quest - ObjectiveText1 : entry=26949
UPDATE `trans_words` SET `content_loc6`='Heal Wounded Trainee' WHERE `word_id` = 182772; -- locales_quest - ObjectiveText1 : entry=26919
UPDATE `trans_words` SET `content_loc6`='Heaped within this boat is a pile of wrapped corpses. And looking east across Lordamere Lake, an opposing dock on Fenris Isle can be seen.$B$BThe fate of the corpses must lie on Fenris Isle.' WHERE `word_id` = 132008; -- locales_quest - offerrewardtext : entry=438
UPDATE `trans_words` SET `content_loc6`='Hear about the World Pillar' WHERE `word_id` = 180386; -- locales_quest - ObjectiveText1 : entry=26243
UPDATE `trans_words` SET `content_loc6`='Heard Westfall was in some deep doody. Well, we got our own set of problems here. See them gnolls out there? Looks like they\'re actin\' up again. I don\'t like it. Don\'t like it one bit.' WHERE `word_id` = 180852; -- locales_quest - offerrewardtext : entry=26365
UPDATE `trans_words` SET `content_loc6`='Hearing their screams... watching the last life fade from their wretched bodies... it\'s invigorating, is it not?$B$BI relish it.' WHERE `word_id` = 159260; -- locales_quest - offerrewardtext : entry=12264
UPDATE `trans_words` SET `content_loc6`='Heart of Arkkoroc identified' WHERE `word_id` = 174626; -- locales_quest - EndText : entry=24452
UPDATE `trans_words` SET `content_loc6`='Hearthglen Trainee challenged and defeated' WHERE `word_id` = 183670; -- locales_quest - ObjectiveText1 : entry=27156
UPDATE `trans_words` SET `content_loc6`='Heartrazor released' WHERE `word_id` = 186835; -- locales_quest - ObjectiveText1 : entry=28088
UPDATE `trans_words` SET `content_loc6`='Heartwood by nature is resistant to disease, rot and plague. This heartwood, however, goes so far as to repel such things! The masonry, affected by the fallout of the Unbound Thicket, will work in much the same way.$B$BSoon we will break ground.' WHERE `word_id` = 162696; -- locales_quest - offerrewardtext : entry=13130
UPDATE `trans_words` SET `content_loc6`='Heavy words, $N. If it were somebody else saying them, I\'d be likely to judge them insane.' WHERE `word_id` = 163675; -- locales_quest - offerrewardtext : entry=13363
UPDATE `trans_words` SET `content_loc6`='Heb\'Drakkar trolls scalped' WHERE `word_id` = 160664; -- locales_quest - ObjectiveText1 : entry=12659
UPDATE `trans_words` SET `content_loc6`='Heh heh, well done! And when you showed Shaky those giblets, how hard did he shake?$B$BHah! He\'s so easy to read, when I play cards with him I can\'t help but win!$B$BThanks for your help, $N. Now it\'s your turn to get paid...' WHERE `word_id` = 132753; -- locales_quest - offerrewardtext : entry=26594
UPDATE `trans_words` SET `content_loc6`='Heh, so old Verner needs some shoes, does he!?!' WHERE `word_id` = 130626; -- locales_quest - offerrewardtext : entry=118
UPDATE `trans_words` SET `content_loc6`='Heh, you\'re kidding, right? The Burning Legion is blaming us for all of that?$B$BBut YOU\'RE the one that did it!$B$BOy, I have a headache and it\'s as big as this rocket. Okay, okay, this calls for a change of plans.$B$BIf the Burning Legion thinks that it\'s going to come here, destroy our town and ruin my dream of cruising through the Twisted Nether, they are sadly mistaken.$B$BAnd I know just the goblin to help us out.' WHERE `word_id` = 150217; -- locales_quest - offerrewardtext : entry=10237
UPDATE `trans_words` SET `content_loc6`='Hehe, ethereal suckers!$B$BThanks for getting us these parts, $C. I bet we can just hook these babies right up inside the rocket. I wonder what they do?  Should be fun finding out!$B$BHmm, let me see if I can dig up some pocket change to throw your way.' WHERE `word_id` = 150085; -- locales_quest - offerrewardtext : entry=10206
UPDATE `trans_words` SET `content_loc6`='Hehe, oh yes... of course, the Carevins.$B$BNo, nothing\'s wrong but it is hard for me to go into town now. The roads are dangerous and I\'m not getting any younger. Maybe you can do an old man some favors?' WHERE `word_id` = 130834; -- locales_quest - offerrewardtext : entry=165
UPDATE `trans_words` SET `content_loc6`='Helgrum wants a report, eh? I sent an outrunner to Stonard not three days past. Did he fail his mission? If Stonard does not have my report, then it appears he did fail.$B$BLet\'s hope the next runner I send has more skill, or more luck.' WHERE `word_id` = 135526; -- locales_quest - offerrewardtext : entry=1418
UPDATE `trans_words` SET `content_loc6`='Helix is the one responsible for the gnoll attacks? He\'s inside. He\'s been there for days. I\'ve just been waiting for an excuse to bust him.' WHERE `word_id` = 180554; -- locales_quest - offerrewardtext : entry=26289
UPDATE `trans_words` SET `content_loc6`='Helix? I\'ve heard that name before. Recently as a matter of fact, but who is this admiral?' WHERE `word_id` = 180542; -- locales_quest - offerrewardtext : entry=26286
UPDATE `trans_words` SET `content_loc6`='Helix\'s Secret Revealed' WHERE `word_id` = 180559; -- locales_quest - EndText : entry=26290
UPDATE `trans_words` SET `content_loc6`='Hell-Hoot is slain, and her meat is just as succulent as you\'d expect.$B$BToday, we eat a legend. Thank you, $N.' WHERE `word_id` = 189256; -- locales_quest - offerrewardtext : entry=28782
UPDATE `trans_words` SET `content_loc6`='Hellions & Wind Riders shot down' WHERE `word_id` = 172603; -- locales_quest - ObjectiveText1 : entry=13853
UPDATE `trans_words` SET `content_loc6`='Hello $N. I have heard of your deeds from Darsok. You are a valuable $C of the Horde, indeed.' WHERE `word_id` = 189773; -- locales_quest - offerrewardtext : entry=29095
UPDATE `trans_words` SET `content_loc6`='Hello $N. You\'re here about the task I have, eh? It\'s good to see a young $R like yourself whose blood burns at the prospect of adventure!' WHERE `word_id` = 132138; -- locales_quest - offerrewardtext : entry=467
UPDATE `trans_words` SET `content_loc6`='Hello again, $C.$B$BI felt L\'ghorek die. A shame that such a magnificent being is gone.$B$BIf we do not assist Neptulon, we will all meet the same fate.' WHERE `word_id` = 180168; -- locales_quest - offerrewardtext : entry=26181
UPDATE `trans_words` SET `content_loc6`='Hello again, $N. How goes your studies of the Tome of Divinity?$B$BThen you should be prepared to have yourself tested. There are always tasks to be done throughout the city and our lands; acts of compassion and understanding; people who need help; creatures to slay.$B$BBy proving you have the patience to help others, especially those who are less fortunate, you prove that you are a servant of the Light and of sound mind.' WHERE `word_id` = 136008; -- locales_quest - offerrewardtext : entry=1642
UPDATE `trans_words` SET `content_loc6`='Hello again, $R. I hear you were instrumental in our push toward Grim Batol - thank you! Alexstrasza has sanctified this ground in the hopes of luring Deathwing here. She\'ll perform a ceremony to lure him out... and then destroy him within this sacred circle. Let\'s hope her plan works.' WHERE `word_id` = 189060; -- locales_quest - offerrewardtext : entry=28712
UPDATE `trans_words` SET `content_loc6`='Hello again, Abyssmal. I am glad that you wish to speak to me once more concerning our neverending need for peacebloom. The war effort continues, and with it our production of potions and elixirs to aid in the battles to come. Will you help us again, $C? I am in need of the same amount of peacebloom as before, so if you can gather it up and return it to me here I would be most grateful. Of course, if you already have it on your person, I will be more than happy to accept it from you now' WHERE `word_id` = 143856; -- locales_quest - details : entry=8550
UPDATE `trans_words` SET `content_loc6`='Hello child.$B$BI understand Tirion\'s request and trust in his judgment, we will see what we can do for this Crusader Bridenbrad. What you ask is no simple task - the scourge are not of this world, and many have failed in attempting to strip their corruption over the years.$B$BIn a being of nobility and light, all is not without hope.' WHERE `word_id` = 162435; -- locales_quest - offerrewardtext : entry=13073
UPDATE `trans_words` SET `content_loc6`='Hello hello, $N. Captain told me you\'re going after Negolash, eh?' WHERE `word_id` = 132785; -- locales_quest - offerrewardtext : entry=615
UPDATE `trans_words` SET `content_loc6`='Hello there - Lord Shadowbreaker sent me word ahead of time that you\'d be coming around this way to see me. I\'m willing to make the barding you need, but it won\'t be either cheap or easy; let\'s get that out in the open first and foremost.$B$BI will say this much, and Grayson will back me up when I make this claim... you\'ll not find better barding made anywhere in all of Azeroth than right here.' WHERE `word_id` = 142284; -- locales_quest - offerrewardtext : entry=7641
UPDATE `trans_words` SET `content_loc6`='Hello there! A new friend! So glad to meet you. My name is Ergll. The g is silent.' WHERE `word_id` = 173373; -- locales_quest - offerrewardtext : entry=14130
UPDATE `trans_words` SET `content_loc6`='Hello there, $G laddy:lass;! The mountaineers sent you over, eh? I do suspect we might have just the sort of work you\'re looking for. Welcome to the Farstrider Lodge!' WHERE `word_id` = 172437; -- locales_quest - offerrewardtext : entry=13647
UPDATE `trans_words` SET `content_loc6`='Hello there. You must be... $N?' WHERE `word_id` = 175802; -- locales_quest - offerrewardtext : entry=24959
UPDATE `trans_words` SET `content_loc6`='Hello!  It\'s me. Hope you don\'t mind a little home-brewed magic to allow me to communicate with you in the field. It\'ll save us both some time. We\'re going to incorporate that living rot into a mixture that\'ll ruin their next batch of plague. That\'ll show them!' WHERE `word_id` = 184498; -- locales_quest - offerrewardtext : entry=27451
UPDATE `trans_words` SET `content_loc6`='Hello!$B$BOh, you spoke to my cousin Krazek?  How is he?  I hope Booty Bay is treating him well...$B$BScarab shells, you say?  Well... let me tell you a story...' WHERE `word_id` = 137318; -- locales_quest - offerrewardtext : entry=2864
UPDATE `trans_words` SET `content_loc6`='Hello, $C! Keegan Firebeard here has told me all about your heroics down south. Thank you for all of your efforts on behalf of the Wildhammer. I see Colin Thundermar is right behind you. The summit is ready to begin! Please stay and let\'s see how things unfold...' WHERE `word_id` = 187692; -- locales_quest - offerrewardtext : entry=28294
UPDATE `trans_words` SET `content_loc6`='Hello, $C! You\'re here for the scouting mission, are you? Very good!$B$BI hope your gear is in good condition, because this "scouting" mission has changed into something more messy...' WHERE `word_id` = 132142; -- locales_quest - offerrewardtext : entry=468
UPDATE `trans_words` SET `content_loc6`='Hello, $C. Is there something I can help you with?' WHERE `word_id` = 138645; -- locales_quest - offerrewardtext : entry=3942
UPDATE `trans_words` SET `content_loc6`='Hello, $C. What\'s that? You say that Shyela sent you? Very well, let\'s get started.' WHERE `word_id` = 172343; -- locales_quest - offerrewardtext : entry=13617
UPDATE `trans_words` SET `content_loc6`='Hello, $C.$B$BYou\'ll be escorting me to the excavation site? That\'s good to hear... After Saean and his cronies, who knows what else the Dark Irons might have up their sleeves?$B$BAnyways, let me just make some final checks. Talk to me again when you\'re ready to go.' WHERE `word_id` = 132082; -- locales_quest - offerrewardtext : entry=454
UPDATE `trans_words` SET `content_loc6`='Hello, $G sonny:lass;! You seem to have found one of our stolen documents, eh?$B$BThank you kindly for bringing it back to us. We\'ll gladly lend some coin as thanks for its return.' WHERE `word_id` = 172468; -- locales_quest - offerrewardtext : entry=13656
UPDATE `trans_words` SET `content_loc6`='Hello, $N!  Glad to see you\'ve rejoined the caravan! Tarenar and Gidwin arrived here before you. Tarenar\'s just right there, and Gidwin\'s... well... he\'s in a little bit of a mood right now. I think he\'s in the tower.' WHERE `word_id` = 184568; -- locales_quest - offerrewardtext : entry=27466
UPDATE `trans_words` SET `content_loc6`='Hello, $N, I have been expecting you. I trust this day finds you well?' WHERE `word_id` = 147098; -- locales_quest - offerrewardtext : entry=9461
UPDATE `trans_words` SET `content_loc6`='Hello, $N. I am Bishop DeLavey.$B$BNo doubt if you\'re asking me about my business here in the keep then young Thomas has succeeded in requisitioning you to help our kingdom. He has always been adept at following my requests.$B$BI\'m afraid I don\'t have time to elaborate much, so allow me to be blunt... and please, try to keep this from becoming common knowledge.' WHERE `word_id` = 135305; -- locales_quest - offerrewardtext : entry=1274
UPDATE `trans_words` SET `content_loc6`='Hello, $N. I have indeed heard your name before, and although you are still full of youth, I am pleased to see the path you have set for yourself.$B$BYou would do me a great honor if you would accept this as a gift of appreciation.' WHERE `word_id` = 134667; -- locales_quest - offerrewardtext : entry=1081
UPDATE `trans_words` SET `content_loc6`='Hello, $N. I\'m assuming by all that armor and weapon of yours, you\'re either here to try and take my land, or the Church finally sent someone to help protect me while my Jordan\'s gone.$B$B Well, let me introduce myself: I\'m Daphne Stilwell. It\'s a pleasure.$B$BNow, you\'re not too late... I\'ve only killed a half dozen of the scum since last sun up. They\'ve been a little slow getting \'round the bend up there, but they probably saw you coming, which means they\'ll probably be here any minute.' WHERE `word_id` = 136031; -- locales_quest - offerrewardtext : entry=1650
UPDATE `trans_words` SET `content_loc6`='Hello, $N. It\'s good to see you again.$B$BYou needn\'t worry. We\'ve settled in quite well. I\'ve dispatched several of my wardens to assist in hunting down the Druids of the Flame, and I\'ve drawn assault plans for the Forlorn Spire. When the time is right, perhaps you will join us there.' WHERE `word_id` = 190464; -- locales_quest - offerrewardtext : entry=29215
UPDATE `trans_words` SET `content_loc6`='Hello, $N. It\'s good to see you made it to Fray Island. I hope you find it to your liking...' WHERE `word_id` = 136215; -- locales_quest - offerrewardtext : entry=1718
UPDATE `trans_words` SET `content_loc6`='Hello, $N. You\'d like information about a specific member of the Theramore guard? Reethe, was it?$B$BJust give me a moment to find the right book.' WHERE `word_id` = 135258; -- locales_quest - offerrewardtext : entry=1259
UPDATE `trans_words` SET `content_loc6`='Hello, $R. I am Baron Sablemane. A friend of Rexxar\'s is a friend of mine.' WHERE `word_id` = 152317; -- locales_quest - offerrewardtext : entry=10783
UPDATE `trans_words` SET `content_loc6`='Hello, $R.$B$BYou don\'t happen to be available for a recovery mission of some importance?' WHERE `word_id` = 150339; -- locales_quest - offerrewardtext : entry=10263
UPDATE `trans_words` SET `content_loc6`='Hello, citizen. This is a dangerous place to be wandering about. I wouldn\'t hang around too long if you value your life.$B$BIf you keep your questions short, I\'ll see if I can help you. Just keep your eyes open for any of those filthy ogres. Rumor has it there\'s a Horde base somewhere in the swamps. The infiltrators are doing what they can to find it, but the process has been slow going and we\'ve had little progress.' WHERE `word_id` = 135281; -- locales_quest - offerrewardtext : entry=1266
UPDATE `trans_words` SET `content_loc6`='Hello, fancy $Gboy:girl;! What\'s a prissy thing like you doing in the Pig and Whistle? Be careful, or you might break a nail...' WHERE `word_id` = 135999; -- locales_quest - offerrewardtext : entry=1639
UPDATE `trans_words` SET `content_loc6`='Hello, fire juggler!  Are you having fun and making fiery mayhem?  That\'s what I like to hear!  Now, do you think you\'re an expert at torch tossing?  Then I have a test for you...$B$BYou know the rules: toss torches at the marked braziers. This time, however, you have a little more time and a LOT more braizers to hit.$B$BReady?' WHERE `word_id` = 154715; -- locales_quest - details : entry=11926
UPDATE `trans_words` SET `content_loc6`='Hello, little $R. Bastia is fast, no? We have something very important that needs doing. Can you help us?' WHERE `word_id` = 173629; -- locales_quest - offerrewardtext : entry=14240
UPDATE `trans_words` SET `content_loc6`='Hello, what is your business in a place like this? I\'m sorry, who sent you? I\'m afraid I\'ve never heard of such a dwarf.' WHERE `word_id` = 184584; -- locales_quest - offerrewardtext : entry=27469
UPDATE `trans_words` SET `content_loc6`='Hello, what is your business in a place like this? So Overseer Oilfist sent you? Listen carefully, we have much to discuss in little time.' WHERE `word_id` = 184587; -- locales_quest - offerrewardtext : entry=27470
UPDATE `trans_words` SET `content_loc6`='Hello, what is your business in a place like this? So you come at the will of Miss Brightwing, do you? Very interesting indeed.' WHERE `word_id` = 184590; -- locales_quest - offerrewardtext : entry=27471
UPDATE `trans_words` SET `content_loc6`='Hello, yes? What? You? Oh, you\'re one of the ones I sent after the Eye of Twilight, aren\'t you? Good work, very good. I suppose that\'s a valuable lesson: Never to take orders from a big purple ball.' WHERE `word_id` = 177072; -- locales_quest - offerrewardtext : entry=25312
UPDATE `trans_words` SET `content_loc6`='Hello, young one. Here for some instruction, are you?' WHERE `word_id` = 135838; -- locales_quest - offerrewardtext : entry=1505
UPDATE `trans_words` SET `content_loc6`='Hello. Do you have business with me?' WHERE `word_id` = 143862; -- locales_quest - offerrewardtext : entry=620
UPDATE `trans_words` SET `content_loc6`='Hello. Hello, hello hello hello hello HELLO!$B$BI\'m sorry - wait, no I\'m not! Don\'t be rude!$B$BOh no...that was crazy talk.$B$BBut...sometimes being crazy is my only solace.' WHERE `word_id` = 133082; -- locales_quest - offerrewardtext : entry=687
UPDATE `trans_words` SET `content_loc6`='Hello. I\'m Pamela, what\'s your name?' WHERE `word_id` = 139905; -- locales_quest - offerrewardtext : entry=27383
UPDATE `trans_words` SET `content_loc6`='Hellscream shall know of your deeds here, $N. Without you, things here would be far worse. Lok\'tar ogar, my friend!' WHERE `word_id` = 189390; -- locales_quest - offerrewardtext : entry=28843
UPDATE `trans_words` SET `content_loc6`='Help Akuno find his way to the Refugee Caravan in Terokkar Forest. Speak to Mekeda after you\'ve completed this quest.' WHERE `word_id` = 152780; -- locales_quest - objectives : entry=10887
