UPDATE `trans_words` SET `content_loc6`='Help me, stranger. I must... reach Tranquillien... I cannot fail.$B$B<The blood elf falls back into unconsciousness as his eyes go blank.>' WHERE `word_id` = 146300; -- locales_quest - offerrewardtext : entry=9144
