UPDATE `trans_words` SET `content_loc6`='Help the Crimson Lasher Drink from Ember Pools' WHERE `word_id` = 190687; -- locales_quest - ObjectiveText1 : entry=29299
UPDATE `trans_words` SET `content_loc6`='Help Thrall break out of his stone prison' WHERE `word_id` = 190741; -- locales_quest - ObjectiveText1 : entry=29329
UPDATE `trans_words` SET `content_loc6`='Help you? Help these people? These people that would rather spit on me than crack a smile?' WHERE `word_id` = 181528; -- locales_quest - offerrewardtext : entry=26567
UPDATE `trans_words` SET `content_loc6`='Help Zen\'Taji' WHERE `word_id` = 176662; -- locales_quest - ObjectiveText4 : entry=25188
UPDATE `trans_words` SET `content_loc6`='Help Zen\'Taji' WHERE `word_id` = 345292; -- locales_quest - CompletedText : entry=25188
UPDATE `trans_words` SET `content_loc6`='Help! Please help!' WHERE `word_id` = 187976; -- locales_quest - offerrewardtext : entry=28362
UPDATE `trans_words` SET `content_loc6`='Help... blood elves! They... ambushed... me. Don\'t have... long.' WHERE `word_id` = 146642; -- locales_quest - offerrewardtext : entry=9309
UPDATE `trans_words` SET `content_loc6`='Helpless Villager Rescued' WHERE `word_id` = 159133; -- locales_quest - ObjectiveText1 : entry=12237
UPDATE `trans_words` SET `content_loc6`='Helpless Young Druid Rescued' WHERE `word_id` = 179131; -- locales_quest - ObjectiveText1 : entry=25889
UPDATE `trans_words` SET `content_loc6`='Hemet Nesingwary Jr. doubted you, but his doubts were unfounded. You are absolutely destroying these raptors.' WHERE `word_id` = 165797; -- locales_quest - offerrewardtext : entry=195
UPDATE `trans_words` SET `content_loc6`='Hemet\'s taste test' WHERE `word_id` = 160598; -- locales_quest - ObjectiveText1 : entry=12645
UPDATE `trans_words` SET `content_loc6`='Her eye looks as if it is still alive. Do you think that she can still see into your soul?$B$B$N, you can now count yourself as one of the elite safari hunters. And you are another step closer to stalking the ultimate prey of this land, Tusker!$B$BYou\'ve earned the right to have this. May it serve you well.' WHERE `word_id` = 148688; -- locales_quest - offerrewardtext : entry=9856
UPDATE `trans_words` SET `content_loc6`='Herald Volazj Defeated' WHERE `word_id` = 162974; -- locales_quest - ObjectiveText2 : entry=13187
UPDATE `trans_words` SET `content_loc6`='Herald\'s Incense Burned' WHERE `word_id` = 178787; -- locales_quest - ObjectiveText1 : entry=25807
UPDATE `trans_words` SET `content_loc6`='Herbs Used in Flasks or Elixirs' WHERE `word_id` = 189543; -- locales_quest - ObjectiveText1 : entry=29067
UPDATE `trans_words` SET `content_loc6`='Here are some Cenarion plant salves - I only need one lesser nether essence for this, $N. Essence is by far the most potent item I can make use of to turn swords into plowshares, as it were. If you need more salve, then find me here!' WHERE `word_id` = 138756; -- locales_quest - offerrewardtext : entry=4107
UPDATE `trans_words` SET `content_loc6`='Here are some weapons, hot off the anvil and made with the new umbral ore you brought me!$B$BPlease, $N, take your pick!' WHERE `word_id` = 136096; -- locales_quest - offerrewardtext : entry=1682
UPDATE `trans_words` SET `content_loc6`='Here are the patterns, $N. I am sure you will find the product of this knowledge quite beautiful.' WHERE `word_id` = 144483; -- locales_quest - offerrewardtext : entry=8878
UPDATE `trans_words` SET `content_loc6`='Here are the wands I made. Take your pick!' WHERE `word_id` = 136521; -- locales_quest - offerrewardtext : entry=1952
UPDATE `trans_words` SET `content_loc6`='Here are your legguards, $N. Let me know how they fit or if you need any modifications. I aim to please! At least I used to.$B$BThese days I don\'t care much for my craft. But it beats being a mindless minion of the Scourge!$B$BI guess.' WHERE `word_id` = 136385; -- locales_quest - offerrewardtext : entry=1847
UPDATE `trans_words` SET `content_loc6`='Here are your robes, $N. Wear them well.' WHERE `word_id` = 136560; -- locales_quest - offerrewardtext : entry=1962
UPDATE `trans_words` SET `content_loc6`='Here goes nothing, $N! This will get Larion\'s attention!' WHERE `word_id` = 138849; -- locales_quest - offerrewardtext : entry=4144
UPDATE `trans_words` SET `content_loc6`='Here here, WOT!? He calls ME a COWARD!? Just like a Firebeard, all hot air and not a lick of sense. I\'ve been bearing the brunt of a Dragonmaw invasion from the south, much of Thundermar is in ruins, and do any of the other clans stick up to help? NO! Ol\' Thundermar is on his own he is, and you\'d better believe I\'m usin\' everything in my arsenal just to stem the tide.' WHERE `word_id` = 186561; -- locales_quest - offerrewardtext : entry=28001
UPDATE `trans_words` SET `content_loc6`='Here in Ironforge, I am responsible for putting paladins on the path to greater virtue as they devote themselves to the Light and defend Azeroth from the Scourge and other threats.$B$BThe time has come for you to take your first step towards being truly virtuous. If you accept it, I will give you a Book of Divinity. In studying it, you shall learn some about the Light, and what is expected of you. If you understand it, and are capable, then you will succeed in attaining greater abilities.' WHERE `word_id` = 136016; -- locales_quest - offerrewardtext : entry=1645
UPDATE `trans_words` SET `content_loc6`='Here in Stormwind, I am responsible for putting paladins on the path to greater virtue as they devote themselves to the Light and defend Azeroth from the Scourge and other threats.$B$BThe time has come for you to take your first step towards being truly virtuous. If you accept it, I will give you a Book of Divinity. In studying it, you shall learn some about the Light, and what is expected of you. If you understand it, and are capable, then you will succeed in attaining greater abilities.' WHERE `word_id` = 136005; -- locales_quest - offerrewardtext : entry=1641
UPDATE `trans_words` SET `content_loc6`='Here is the antidote, $N. Take it to Rhinag as soon as you can, and be cautious.' WHERE `word_id` = 133571; -- locales_quest - offerrewardtext : entry=813
UPDATE `trans_words` SET `content_loc6`='Here is the corpse of a dwarf -- mauled, frozen, and picked clean by mountain scavengers. Clutched in its hand is a book containing the roughly scribbled notes of Mori Hildelve. And around its frame, still in superior condition, is a Brigadier\'s Vest.$B$BThis grisly scene must have been where the Siege Engine pilot met his end.' WHERE `word_id` = 269748; -- locales_quest - offerrewardtext : entry=26854
UPDATE `trans_words` SET `content_loc6`='Here is your Dalaran Jewelcrafter\'s Token as promised. Come back tomorrow and I am sure I will have another item I need from you!' WHERE `word_id` = 161958; -- locales_quest - offerrewardtext : entry=12958
UPDATE `trans_words` SET `content_loc6`='Here is your Dalaran Jewelcrafter\'s Token as promised. I have new shipments going out all the time, check by from time to time to see what I need.' WHERE `word_id` = 162298; -- locales_quest - offerrewardtext : entry=13041
UPDATE `trans_words` SET `content_loc6`='Here is your first Gnome Engineer Membership Card!$B$BThis card has its dues paid for in advance for fourteen days; all cards expire after two weeks of activity. When it does, pay your next set of dues to any gnome engineer trainer in order to get another card; without the card, your membership will temporarily lapse and you cannot access new schemata.$B$BThis never affects what you already know; once a gnome engineer, always a gnome engineer.' WHERE `word_id` = 138399; -- locales_quest - offerrewardtext : entry=3641
UPDATE `trans_words` SET `content_loc6`='Here is your first Goblin Engineer Membership Card!$B$BThis card has its dues paid for in advance for fourteen days; all cards expire after two weeks of activity. When it does, pay your next set of dues to any goblin engineer trainer in order to get another card; without the card, your membership will temporarily lapse and you cannot access new schemata.$B$BThis never affects what you already know; once a goblin engineer, always a goblin engineer.' WHERE `word_id` = 138391; -- locales_quest - offerrewardtext : entry=3639
UPDATE `trans_words` SET `content_loc6`='Here is your nether-lace, $N. It is both comfortable and durable, and holds the magic of the laughing sisters\' hair.$B$BEnjoy, and if you find it amenable, please mention so to Deino. She is a mage on whose good side I wish to stay...' WHERE `word_id` = 136494; -- locales_quest - offerrewardtext : entry=1946
UPDATE `trans_words` SET `content_loc6`='Here is your order, $N. Delivered as promised!' WHERE `word_id` = 145242; -- locales_quest - offerrewardtext : entry=9234
UPDATE `trans_words` SET `content_loc6`='Here is your payment ... and while you\'re here, take a look around! I\'m sure we have a potion or other trinket you\'d find useful.' WHERE `word_id` = 130380; -- locales_quest - offerrewardtext : entry=61
UPDATE `trans_words` SET `content_loc6`='Here is your robe. It\'s one of my best made yet!  Wear it with pride, and when people ask... tell them you got it at the Larson Clothiers!' WHERE `word_id` = 136476; -- locales_quest - offerrewardtext : entry=1941
UPDATE `trans_words` SET `content_loc6`='Here is your schematic. Study it closely, and learn...$B$BGood luck, $N. May your celebrations rival that of the Lunar Festival itself!' WHERE `word_id` = 144491; -- locales_quest - offerrewardtext : entry=8882
UPDATE `trans_words` SET `content_loc6`='Here it is - the Hand of Iruxos.$B$BGot it from one o\' them Burning Blade leaders myself.$B$BPowerful thing! See, yeh use it on demon portals and they just disappear! Spooky magic, but these be desperate times....$B$BThe demons keep comin\' at this rate, and my camp will get overrun before ya know it.$B$BHere ya go, now. Get on with it.' WHERE `word_id` = 275192; -- locales_quest - details : entry=14374
UPDATE `trans_words` SET `content_loc6`='Here it is, $N, the proof of my crime. This journal contains all of my ill-conceived studies.$B$BHeedless of the warnings, I continued my research, until it was almost too late. Then, when I grasped my error, I did what I could. But by then, some of my pupils had already gone too far. I evacuated the Spire to get them away from the corruption, but in my haste, forgot to shutdown the power sources.$B$BI will burn this tome so that no one else falls prey to its contents. I only wanted to help the sin\'dorei.' WHERE `word_id` = 146175; -- locales_quest - offerrewardtext : entry=8891
UPDATE `trans_words` SET `content_loc6`='Here it is, $N. A weapon worthy of a champion of the Light such as yourself. I\'ve named it after a great dwarven paladin who was a friend of mine. His name was Verigan... Bosch Verigan.$B$BPlease take it on behalf of myself and my wife. We owe you a great debt, and the world will be a safer place with one of your skill traveling the land.' WHERE `word_id` = 136319; -- locales_quest - offerrewardtext : entry=1806
UPDATE `trans_words` SET `content_loc6`='Here it is, $N. Remember, ya have ta carry it with you. Why don\'t ya head back to da wilds, an\' see what ya be findin\' there?' WHERE `word_id` = 166751; -- locales_quest - offerrewardtext : entry=975
UPDATE `trans_words` SET `content_loc6`='Here it is, $N. Use it well in the service of Silvermoon and your order.' WHERE `word_id` = 148056; -- locales_quest - offerrewardtext : entry=9710
UPDATE `trans_words` SET `content_loc6`='Here now. Ye hear that? There be sounds in them hills. Those\'re the wolpertingers! Wily beasts, they are, attracted by the smells o\' Brewfest. I\'ve a mind to catch me some, but I knocked me shin and can\'t outrun the buggers. Ye can help me, though!$B$BTake this net and cast it over the little blighters. Bring me a good five of \'em and I\'ll make it worth yer while! Oh, and one more thing - in order to even see these critters, ye need t\' be blitzed! Load up on the booze!' WHERE `word_id` = 153700; -- locales_quest - details : entry=11117
UPDATE `trans_words` SET `content_loc6`='Here so soon! Lovely trip I hope. Not too nauseous or anything? There will be much running and much jumping, so a settled stomach is of great practicality.' WHERE `word_id` = 174303; -- locales_quest - offerrewardtext : entry=14413
UPDATE `trans_words` SET `content_loc6`='Here to get in on the bounty action, eh?' WHERE `word_id` = 158991; -- locales_quest - offerrewardtext : entry=12208
UPDATE `trans_words` SET `content_loc6`='Here we are deep within the realm of the fire god and what do we find? The little puppets of Ragnaros, scurrying around to do his bidding.$B$BThese Dark Iron laborers are finishing and packaging the weapons Twilight\'s Hammer hopes to use in their conquest of Hyjal. I don\'t care if they\'re here of their own free will or if they\'ve been brainwashed or kidnapped... They\'re a foe of our beloved Wolf Ancient and they should pay the ultimate price.' WHERE `word_id` = 177898; -- locales_quest - details : entry=25576
UPDATE `trans_words` SET `content_loc6`='Here we are, $N! Great things will happen here... magical things!' WHERE `word_id` = 187613; -- locales_quest - offerrewardtext : entry=28274
UPDATE `trans_words` SET `content_loc6`='Here we are, $N. He is a little rotted, but no worse for the wear. We must hurry before the Alliance finds us!' WHERE `word_id` = 184384; -- locales_quest - offerrewardtext : entry=27406
UPDATE `trans_words` SET `content_loc6`='Here ya go $N - put this thing on, and you\'ll have ten minutes to do whatever. Good luck!$B$BOh, and if you happen to find a key to get me out of here, BY ALL MEANS LET ME KNOW!!!' WHERE `word_id` = 140413; -- locales_quest - offerrewardtext : entry=27120
UPDATE `trans_words` SET `content_loc6`='Here you are, $C. With this you should find maneuvering in the sea to be nearly effortless. $B$BNow we must get to work. There is much to be done in little time.' WHERE `word_id` = 174554; -- locales_quest - offerrewardtext : entry=24432
UPDATE `trans_words` SET `content_loc6`='Here you are, $N. Here are Darkmoon Faire tickets for your enjoyment. Do not, as they say, spend them all in one place!' WHERE `word_id` = 142714; -- locales_quest - offerrewardtext : entry=7942
UPDATE `trans_words` SET `content_loc6`='Here you are, $N. Here is the schematic to create a firework launcher. Place launchers where you wish to celebrate, and then welcome your friends to join you!' WHERE `word_id` = 144481; -- locales_quest - offerrewardtext : entry=8877
UPDATE `trans_words` SET `content_loc6`='Here you are, $N. I will now give ya the power to tame a beast, as well as call it and dismiss it as you please.$B$BFind yerself a good, loyal pet, and enjoy the hunt!' WHERE `word_id` = 140972; -- locales_quest - offerrewardtext : entry=6085
UPDATE `trans_words` SET `content_loc6`='Here you are, $N. One more Twilight medallion of station. Use it well, and I wish you luck against the Abyssal Council!' WHERE `word_id` = 143332; -- locales_quest - offerrewardtext : entry=8333
UPDATE `trans_words` SET `content_loc6`='Here you are, $N. Take these recipes and learn their secrets. I bid you fortune, and I look forward to seeing your magnificent creations.' WHERE `word_id` = 144487; -- locales_quest - offerrewardtext : entry=8880
UPDATE `trans_words` SET `content_loc6`='Here you go, $N. And please tell Menara we say hello.$B$BWe expect great things of you. Perhaps when the Legion falls to its knees, your name will be spoken aloud and in praise of your power. I can\'t wait!' WHERE `word_id` = 139426; -- locales_quest - offerrewardtext : entry=4785
UPDATE `trans_words` SET `content_loc6`='Here you go, $N. Try these on.' WHERE `word_id` = 133211; -- locales_quest - offerrewardtext : entry=716
UPDATE `trans_words` SET `content_loc6`='Here you go, here you go!  A prize fit for a king!  Congratulations to you, my friend!$B$BIf you have more tickets you\'d like to turn in, then please let me know!  For as long as the Darkmoon Faire is here, I\'ll redeem your tickets.' WHERE `word_id` = 146522; -- locales_quest - offerrewardtext : entry=9249
UPDATE `trans_words` SET `content_loc6`='Here you go, here you go! A prize fit for a $Gking:queen;! Congratulations to you, my friend!$B$BIf you have more tickets you\'d like to turn in, then please let me know! For as long as the Darkmoon Faire is here, I\'ll redeem your tickets.' WHERE `word_id` = 142690; -- locales_quest - offerrewardtext : entry=7930
UPDATE `trans_words` SET `content_loc6`='Here, let me read those plans.$B$BThe blood elves have followed us here?  They are evil to the core and should be wiped from existence!$B$B<The vindicator takes a moment to regain his composure.>$B$BI apologize, that was uncalled for. We are indebted to you for uncovering this information, $N.' WHERE `word_id` = 148420; -- locales_quest - offerrewardtext : entry=9798
UPDATE `trans_words` SET `content_loc6`='Here, take some coin... get yerself... <hic>... some new armor or something....$B$BThis\'ll bring a shiny gold or two to the right... <hic> people.' WHERE `word_id` = 138285; -- locales_quest - offerrewardtext : entry=3542
UPDATE `trans_words` SET `content_loc6`='Here, take the egg... better you have it so it bonds to you and looks to you to be its dadda.$B$BCongrats, $N. You\'re a parent... kinda.' WHERE `word_id` = 139045; -- locales_quest - offerrewardtext : entry=4298
UPDATE `trans_words` SET `content_loc6`='Here\'s my standard issue flare gun, and be patient after you fire off the gun--there are only a couple charges in one of these, and it might take me a minute to get to you depending on how far out to sea I am. Just look out to sea after you fire off the flare.' WHERE `word_id` = 138122; -- locales_quest - offerrewardtext : entry=3451
UPDATE `trans_words` SET `content_loc6`='Here\'s to hopin\' those treacherous bilge rats learned their lessons! Now it be time to man the big\'un, matey!' WHERE `word_id` = 156807; -- locales_quest - offerrewardtext : entry=11527
UPDATE `trans_words` SET `content_loc6`='Here\'s your armor, $N. Furen\'s process worked brilliantly! Perfectly! Marvelously, truly!$B$BYou could run this armor over with a siege engine and it would spring right back into shape!' WHERE `word_id` = 136188; -- locales_quest - offerrewardtext : entry=1709
UPDATE `trans_words` SET `content_loc6`='Here\'s your armor, $N. This will stand up to the strongest assaults or my name isn\'t Furen Longbeard!$B$BAnd I have to thank you. I learned a lot while working with the materials$Byou brought me. I think I\'ll have another breakthrough one day!$B$BI\'m going to send what I learned to other$Bblacksmiths of the Alliance.$B$BI\'ll tell them you were a big help, and maybe they\'ll want to return the favor.' WHERE `word_id` = 136248; -- locales_quest - offerrewardtext : entry=1782
UPDATE `trans_words` SET `content_loc6`='Here\'s your ring, better than before. Thanks again for the information about those Dark Iron dwarves. This seems like a reasonable lead as to how those gnomes suddenly were struck with goblin-like inspiration.' WHERE `word_id` = 137537; -- locales_quest - offerrewardtext : entry=2950
UPDATE `trans_words` SET `content_loc6`='Herod is slain! The Armory will fall!' WHERE `word_id` = 182951; -- locales_quest - offerrewardtext : entry=26962
UPDATE `trans_words` SET `content_loc6`='Heroes: If you are reading this, then my suspicions were correct. Deathwing has found us. The egg is destroyed, as am I. This was, actually, the plan from the beginning. You see... the egg that Deathwing destroyed was not the egg he sought. It was mine.' WHERE `word_id` = 186277; -- locales_quest - offerrewardtext : entry=27930
UPDATE `trans_words` SET `content_loc6`='Hey - you must be that $G fella\': lass; that Wulfred was talkin\' about!  The one who found the ogre artifact, and helped him test out those strange stones from up north. Welcome to the digsite, $N!$B$BWe were actually working on identifying those samples right as you got here. Would you mind bringing the sample over to Dask?  I need him to help me find out how old it is.' WHERE `word_id` = 182450; -- locales_quest - details : entry=26825
UPDATE `trans_words` SET `content_loc6`='Hey - you must be that $G fella\': lass; that Wulfred was talkin\' about!  Welcome to the digsite, $N.$B$BWe were actually just starting a project when you arrived. Would you mind bringing this sample over to Dask?  I need him to help me find out how old it is.' WHERE `word_id` = 182454; -- locales_quest - details : entry=26826
UPDATE `trans_words` SET `content_loc6`='Hey hey mon, did Dizzywig send you? Good, good. Did you bring the money? No? ...what about the guns?' WHERE `word_id` = 173195; -- locales_quest - offerrewardtext : entry=14052
UPDATE `trans_words` SET `content_loc6`='Hey hey!  Not bad, not bad at all!  You may have a future in resource acquisition. Or hotel management.' WHERE `word_id` = 181226; -- locales_quest - offerrewardtext : entry=26487
UPDATE `trans_words` SET `content_loc6`='Hey Hol\'anyee, look at the getup on this one! Ahem. Our recruiter sent you?  Good. Let\'s start out with a bit of honesty: Un\'Goro isn\'t the perfect nirvana that you may have been promised. You\'ve probably figured that out on the way over here, though. Shall we get to work?' WHERE `word_id` = 175624; -- locales_quest - offerrewardtext : entry=24911
UPDATE `trans_words` SET `content_loc6`='Hey now, where ya be gettin\' this?$B$BNo, no, don\'t tell me - this be comin\' from the Hex-Lord himself! It all be clear now. Strong mojo! The bearer of this relic, no matter how many disgraces he be sufferin\', be always honored by his peers. Incredible!$B$B<As Griftah holds the doll in the air, a small, rusty charm falls from around its neck. The troll begins to discard it, but then turns toward you with a gleam in his eye.>$B$BI gots no coins right now, mon, but ya be welcome to this here...' WHERE `word_id` = 154002; -- locales_quest - offerrewardtext : entry=11195
UPDATE `trans_words` SET `content_loc6`='Hey there $C, I need you to solve the Cataclysm. Go hunt me some zombies.' WHERE `word_id` = 174648; -- locales_quest - details : entry=24460
UPDATE `trans_words` SET `content_loc6`='Hey there!  I\'m a crazy dwarf!  We dwarves believe when you get enough brew together, invisible creatures show up. In fact they are probably wandering around the Brewfest grounds right now! But you can only see them if you are really drunk.$B$BWe dwarves will come up with any excuse to drink.$B$BI bet you want a reason to drink too... So here it is, bring me 5 of those wolpertingers with this net I got from a really drunk dwarf.$B$BIf you can\'t see them, you\'re probably not drunk enough.' WHERE `word_id` = 154339; -- locales_quest - details : entry=11431
UPDATE `trans_words` SET `content_loc6`='Hey there! Who are you?$B$BBixie sent you? Excellent, let\'s get me out of here!' WHERE `word_id` = 157415; -- locales_quest - offerrewardtext : entry=11697
UPDATE `trans_words` SET `content_loc6`='Hey there, hon. Vol\'jin sent you? So sweet of him to be thinkin\' of me. I surely could use da help, but ya have a bit more walkin\' ahead of ya.' WHERE `word_id` = 176207; -- locales_quest - offerrewardtext : entry=25064
UPDATE `trans_words` SET `content_loc6`='Hey there. I\'m glad to see they sent someone of your caliber for this operation. This here\'s going to call for some subtlety. We can\'t risk Commander Shadowsong getting killed.' WHERE `word_id` = 177987; -- locales_quest - offerrewardtext : entry=25597
UPDATE `trans_words` SET `content_loc6`='Hey there... again. Look, I gave you my hat. It was a gift. I thought you\'d make some use of it. More specifically though, I thought you\'d get it the heck away from me. Can you imagine my surprise when Xylem comes by himself to give it back to me? "Want to be more like my other apprentices, Sorata? Give your hat another try."  Not one of my proudest moments. So what... you just tossed it aside for him to find? No. I don\'t think so. You\'re going to take this hat and you\'re going to like it. Really.' WHERE `word_id` = 174216; -- locales_quest - offerrewardtext : entry=14392
UPDATE `trans_words` SET `content_loc6`='Hey you! Good to see you again!$B$BI\'m settled in good and fine, actually! It\'s good to be with good old Damek again. I never thought I\'d be mixing with a dwarf so well, but he and I, we\'ve got like minds on all the important stuff.$B$BYou go do your thing. Don\'t be worrying about us. If we cook up any good toys for you, we\'ll be sure to let ya know!' WHERE `word_id` = 190620; -- locales_quest - offerrewardtext : entry=29282
UPDATE `trans_words` SET `content_loc6`='Hey! Get me out of here!' WHERE `word_id` = 177959; -- locales_quest - offerrewardtext : entry=25590
UPDATE `trans_words` SET `content_loc6`='Hey! I know you. Your victory at the Ring of Blood was talked about for months! Too bad you forfeited the championship when you failed to show up to defend your title! Easy come, easy go, eh? At least you\'re still alive.' WHERE `word_id` = 161933; -- locales_quest - offerrewardtext : entry=12954
UPDATE `trans_words` SET `content_loc6`='Hey! Wait just one damned minute!!$B$BThis doesn\'t look good...' WHERE `word_id` = 151956; -- locales_quest - offerrewardtext : entry=10674
UPDATE `trans_words` SET `content_loc6`='Hey, is that what I think it is? Great!$B$BNow, let\'s see. Ugh... how am I supposed to understand this stuff... can\'t figure out the way gnomes think. Lemme tell you, I\'m surprised they could build the wheel, let alone those flying machines they like so much.' WHERE `word_id` = 134722; -- locales_quest - offerrewardtext : entry=1093
UPDATE `trans_words` SET `content_loc6`='Hey, it\'s you again! Well, I mean you - as in the you that is $N! Yes - that you!$B$BHey, let me start over!$B$BThat object you found in Silithus - it was in fact a book. It was a journal written in a very ancient dialect of what we now call the Darnassian tongue. It\'ll take a while, but I\'m going to go over it very thoroughly. I thought you should know that what you found was extremely valuable for the Circle.$B$BThe den mother\'s all excited and everything!' WHERE `word_id` = 135076; -- locales_quest - offerrewardtext : entry=1185
UPDATE `trans_words` SET `content_loc6`='Hey, look at how glowy it is!$B$B<Orako looks wistfully off into the distance.>$B$BMy wolf loved things that were glowy...' WHERE `word_id` = 179849; -- locales_quest - offerrewardtext : entry=26087
UPDATE `trans_words` SET `content_loc6`='Hey, look at that!  You weren\'t blown to bits!' WHERE `word_id` = 165390; -- locales_quest - offerrewardtext : entry=25285
UPDATE `trans_words` SET `content_loc6`='Hey, mon! Thank ya for comin\'.$B$BWe weren\'t ready for dis kind of attack. We been expectin\' da Twilight Cult, but not elementals... we\'ve got no idea what upset dem so much that dey would turn on us like dis.$B$BYou haven\'t been out dere killin\' them, have you?' WHERE `word_id` = 180712; -- locales_quest - offerrewardtext : entry=26326
UPDATE `trans_words` SET `content_loc6`='Hey, nice work! Keep this up and the other rogues might stop spitting on you.' WHERE `word_id` = 141615; -- locales_quest - offerrewardtext : entry=6701
UPDATE `trans_words` SET `content_loc6`='Hey, nice work!$B$BIf you ever want to come back and kill some more bugs for absolutely no additional reward, please, feel free!' WHERE `word_id` = 189024; -- locales_quest - offerrewardtext : entry=28703
UPDATE `trans_words` SET `content_loc6`='Hey, nice! I\'ll just take those... carefully... Don\'t go tellin\' the workers I didn\'t have the guts to go down there myself, you hear? Here\'s your pay... and a little somethin\' extra.' WHERE `word_id` = 187877; -- locales_quest - offerrewardtext : entry=28340
UPDATE `trans_words` SET `content_loc6`='Hey, now isn\'t that just the thing! You\'ve got our supplies back for us. That\'s great. I\'ll be able to get back to work just as soon as I can find someone to haul these sacks for me.' WHERE `word_id` = 133656; -- locales_quest - offerrewardtext : entry=834
UPDATE `trans_words` SET `content_loc6`='Hey, thanks, kid. Give it over, let\'s take a look. Oh, that ain\'t good.' WHERE `word_id` = 275231; -- locales_quest - offerrewardtext : entry=24536
UPDATE `trans_words` SET `content_loc6`='Hey, thanks, kid. Give it over, let\'s take a look.$B$B<He takes the device from you and fiddles with it.>$B$BOh, that ain\'t good.' WHERE `word_id` = 336293; -- locales_quest - offerrewardtext : entry=24655
UPDATE `trans_words` SET `content_loc6`='Hey, that wasn\'t too bad... are you sure you\'re not a dwarf?$B$BSo there\'s plenty more racing to be had and it\'s not just from me. All over the Brewfest grounds you\'ll find people in need of a decent ram racer. So go and make yourself useful.$B$BOh and don\'t forget, these rams were legally obtained. I just want to make sure we\'re clear.' WHERE `word_id` = 154325; -- locales_quest - offerrewardtext : entry=11409
UPDATE `trans_words` SET `content_loc6`='Hey, watch where you\'re pointing that thing! You\'ve got it set to \'Kill\'!!!$B$BIt looks like you\'ve recharged the battery, and then some. This\'ll help immensely to keep these guys going.' WHERE `word_id` = 150006; -- locales_quest - offerrewardtext : entry=10190
UPDATE `trans_words` SET `content_loc6`='Hey, you have been saving! I\'m so proud of you! Um, boss... this isn\'t nearly enough.' WHERE `word_id` = 173331; -- locales_quest - offerrewardtext : entry=14122
UPDATE `trans_words` SET `content_loc6`='Hey, you made it back alive!  Can\'t say you were the first one to try this task, but you are definitely the first one to make it back alive.' WHERE `word_id` = 186420; -- locales_quest - offerrewardtext : entry=27967
UPDATE `trans_words` SET `content_loc6`='Hey, you\'re one big strong heap of $C. Can you handle the crucible? We got bookies are standing by!' WHERE `word_id` = 186668; -- locales_quest - offerrewardtext : entry=28038
UPDATE `trans_words` SET `content_loc6`='Hey, you\'re tougher than you look. These terrortooth hides reflect arrows and turn axe-blades - they don\'t go down easy, but their hides are worth their weight in silver in these light-forsaken Barrens. Nice work.' WHERE `word_id` = 175958; -- locales_quest - offerrewardtext : entry=25000
UPDATE `trans_words` SET `content_loc6`='Hey. man. that\'s great. We hear you\'ve been doing good things. That\'s cool. We\'ve got a feeling that you\'re going to make things right for us here in Ogri\'la, and keep us safe, and everything nice and mellow.$B$BHere, take this. It\'s nothing much, just a care package that we whipped up as a thanks for your effort.$B$BFeel free to look around at our goods. We have a lot of really choice stuff that you\'re gonna want to get.' WHERE `word_id` = 153635; -- locales_quest - offerrewardtext : entry=11091
UPDATE `trans_words` SET `content_loc6`='Hey. You got some time on your hands? I could use someone like you.' WHERE `word_id` = 275282; -- locales_quest - offerrewardtext : entry=24804
UPDATE `trans_words` SET `content_loc6`='Heya, kid, I\'ll be right with ya. Just gotta shut this gnome twerp up first.' WHERE `word_id` = 177604; -- locales_quest - offerrewardtext : entry=25489
UPDATE `trans_words` SET `content_loc6`='Heya, pal, mind if I take a look at that list? What\'s Pozzik thinking? He knows that I don\'t have even half of that stuff!' WHERE `word_id` = 177663; -- locales_quest - offerrewardtext : entry=25505
UPDATE `trans_words` SET `content_loc6`='Hi friend! I don\'t think I know you... I\'m Donnelly Dunwald. What\'s your name? I don\'t think I know you... Do I know you?' WHERE `word_id` = 185226; -- locales_quest - offerrewardtext : entry=27643
UPDATE `trans_words` SET `content_loc6`='Hi there! The fun\'s already started!' WHERE `word_id` = 176420; -- locales_quest - offerrewardtext : entry=25121
UPDATE `trans_words` SET `content_loc6`='Hi there, $N. Yes, what Krakle told you is true...' WHERE `word_id` = 134275; -- locales_quest - offerrewardtext : entry=980
UPDATE `trans_words` SET `content_loc6`='Hi! Erelas sent you? Great, I was just preparing to travel up to the caves now, and I wouldn\'t mind company.' WHERE `word_id` = 134271; -- locales_quest - offerrewardtext : entry=979
UPDATE `trans_words` SET `content_loc6`='Hi! Really glad see you again!$B$BI knew we friends. I knew it! You good friend. Not just for us, but for all Oracles.$B$BI saw you bonk that puppy-man good! Undead thing said he your friend before? I not know that. You done with puppy-men though, yeah? Just Oracle friend now?$B$BI know you choose right! Thank you for saving us! Thank you, thank you!' WHERE `word_id` = 160821; -- locales_quest - offerrewardtext : entry=12689
UPDATE `trans_words` SET `content_loc6`='Hiccup! Ho Ho!' WHERE `word_id` = 130326; -- locales_quest - offerrewardtext : entry=48
UPDATE `trans_words` SET `content_loc6`='Hiccup!$B$BI\'m a-lookin\' right at you but you keep bobbing up and down. Makin\' me dizzy. Hoho!$B$BHiccup!$B$BWhoa! Did ya feel that? Ground is a-shakin\', my friend. I need a drink.$B$BHiccup!' WHERE `word_id` = 135659; -- locales_quest - offerrewardtext : entry=1451
UPDATE `trans_words` SET `content_loc6`='Hidden caves on the backside of the island. How very interesting.$B$BWell, at least now I know where all of my missing spies are.' WHERE `word_id` = 161480; -- locales_quest - offerrewardtext : entry=12847
