UPDATE `trans_words` SET `content_loc6`='Highland Raptors slain' WHERE `word_id` = 179025; -- locales_quest - ObjectiveText1 : entry=25864
UPDATE `trans_words` SET `content_loc6`='Hilda\'s sending us weapons?  That\'s excellent news, $N. I was about ready to tell the lads to pack things up.' WHERE `word_id` = 157987; -- locales_quest - offerrewardtext : entry=11963
