UPDATE `trans_words` SET `content_loc6`='Hsssssssssssss!  Have you come to mock me, $R?' WHERE `word_id` = 157076; -- locales_quest - offerrewardtext : entry=11620
UPDATE `trans_words` SET `content_loc6`='Hugh Glass Questioned' WHERE `word_id` = 158969; -- locales_quest - ObjectiveText1 : entry=12204
UPDATE `trans_words` SET `content_loc6`='Huh? Well that sucks! Darn, I made thingsh worsh. In fact, I think I perfected their plague for them ...hic!$B$B<Peppy grimaces and takes a huge gulp of whatever noxious brew she\'s been drinking.>$B$BWell, I wash hoping that it wouldn\'t come down to thish, but it looks like there\'sh no alternative ...hic.' WHERE `word_id` = 156384; -- locales_quest - offerrewardtext : entry=11330
UPDATE `trans_words` SET `content_loc6`='Huh? What?$B$BBonker who?$B$BI\'ve no idea who you\'re talking about, but I\'ll take your word for it. If you rescued him or her from the pit of Magmoth then I suppose you deserve a reward.$B$BLet me see what I\'ve got lying around in here....' WHERE `word_id` = 157308; -- locales_quest - offerrewardtext : entry=11673
