UPDATE `trans_words` SET `content_loc6`='Leryssa... is looking for me?  You must help me, $N. We must finish this before she gets hurt.' WHERE `word_id` = 158476; -- locales_quest - offerrewardtext : entry=12088
UPDATE `trans_words` SET `content_loc6`='Less gilblins is good.$B$BI\'m glad that they\'re so primitive. I don\'t think I\'d want to be dealing with a race of aquatic tech-freaks right now.' WHERE `word_id` = 179441; -- locales_quest - offerrewardtext : entry=25969
UPDATE `trans_words` SET `content_loc6`='Less gill goblins is good! I\'m glad that they\'re so primitive. I don\'t think I\'d want to be dealing with a race of aquatic tech-freaks right now. Not at all.' WHERE `word_id` = 179154; -- locales_quest - offerrewardtext : entry=25894
