UPDATE `trans_words` SET `content_loc6`='Light be praised, I knew you\'d do it! Arazzius and his minions are no more! Soon, we\'ll have the Legion running for home and this battered world will be safe once more. Then, when justice has been met, we can return home as well.$B$BThanks you, $N - the Alliance and the Sons of Lothar honor you this day!' WHERE `word_id` = 150888; -- locales_quest - offerrewardtext : entry=10400
UPDATE `trans_words` SET `content_loc6`='Lightforge Iron is a precious metal, but it sounds like your quest is a dire one. You\'ve come a long way, $GBrother:Sister;. With luck, you might just find what you seek!$B$BAnd if you do get the iron, I know who can fashion it into a weapon against this Necromancer you speak of in Duskwood.' WHERE `word_id` = 131279; -- locales_quest - offerrewardtext : entry=270
UPDATE `trans_words` SET `content_loc6`='Lighthouse Fire Lit' WHERE `word_id` = 172716; -- locales_quest - ObjectiveText1 : entry=13890
UPDATE `trans_words` SET `content_loc6`='Lightning Strikes Absorbed' WHERE `word_id` = 151883; -- locales_quest - ObjectiveText1 : entry=10657
UPDATE `trans_words` SET `content_loc6`='Like a well-oiled machine.' WHERE `word_id` = 186911; -- locales_quest - offerrewardtext : entry=28108
UPDATE `trans_words` SET `content_loc6`='Lila\'s pet Nibbler got into my workshop again!$B$BI was making a bracelet, to be delivered today. The work was almost done, except for the gems. I spent all night cutting them, but when I stepped out for a bit of breakfast, the little beast came into my workshop and swallowed every gem in sight!  The thing can digest ANYTHING, and trust me, you\'d break your back trying to pick up its leavings.$B$BI need to get replacement gems fast; the courier is coming in tonight. Help!' WHERE `word_id` = 176356; -- locales_quest - details : entry=25105
UPDATE `trans_words` SET `content_loc6`='Lilian\'s Mission Accomplished' WHERE `word_id` = 176139; -- locales_quest - ObjectiveText1 : entry=25046
UPDATE `trans_words` SET `content_loc6`='Lilith wants a boulder moved inside the observatory, eh? That\'s my business: consolidated materials. I usually don\'t get individual buyers, but hey, I won\'t ask questions.' WHERE `word_id` = 188765; -- locales_quest - offerrewardtext : entry=28609
UPDATE `trans_words` SET `content_loc6`='Lirastrasza freed' WHERE `word_id` = 185448; -- locales_quest - ObjectiveText1 : entry=27700
UPDATE `trans_words` SET `content_loc6`='Lirastraza sent me ahead to the lair, suspecting the black dragon we are seeking may still be here. Judging by the hatchlings, it seems she was not mistaken.' WHERE `word_id` = 187203; -- locales_quest - offerrewardtext : entry=28176
UPDATE `trans_words` SET `content_loc6`='Lissen up, mon. Dere be dangerous work ta be done!' WHERE `word_id` = 162540; -- locales_quest - offerrewardtext : entry=13097
UPDATE `trans_words` SET `content_loc6`='Listen as Duarn speaks to Nespirah' WHERE `word_id` = 179278; -- locales_quest - ObjectiveText1 : entry=25922
UPDATE `trans_words` SET `content_loc6`='Listen carefully, $R....' WHERE `word_id` = 172347; -- locales_quest - offerrewardtext : entry=13618
UPDATE `trans_words` SET `content_loc6`='Listen closely, little $R. Time is of the essence.' WHERE `word_id` = 160515; -- locales_quest - offerrewardtext : entry=12628
UPDATE `trans_words` SET `content_loc6`='Listen to Doomsayer\'s speech' WHERE `word_id` = 171804; -- locales_quest - ObjectiveText1 : entry=25228
