UPDATE `trans_words` SET `content_loc6`='Lymel wants you to head up to Silvermoon City and bring back her goods. huh? Sure. I can get you there quickly... for a small fee, of course.' WHERE `word_id` = 146261; -- locales_quest - offerrewardtext : entry=9130
