UPDATE `trans_words` SET `content_loc6`='Magus Bisp "spoken with"' WHERE `word_id` = 183662; -- locales_quest - ObjectiveText1 : entry=27154
UPDATE `trans_words` SET `content_loc6`='Mah\'duun enjoys fighting constructs; they are less messy than organic beings.$B$BThe bounty is yours.$B$BReturn again tomorrow and the winds will have whispered once more to Mah\'duun.' WHERE `word_id` = 154286; -- locales_quest - offerrewardtext : entry=11387
UPDATE `trans_words` SET `content_loc6`='Mahka sent you?$B$B<Mankrik\'s fury quells for a moment.>$B$BIt is a small comfort. But nothing can console me until I am done here.' WHERE `word_id` = 175046; -- locales_quest - offerrewardtext : entry=24604
