UPDATE `trans_words` SET `content_loc6`='Maurin wants you to deliver Maurin\'s Concoction to Karnum at Karnum\'s Grove.' WHERE `word_id` = 174047; -- locales_quest - objectives : entry=14343
UPDATE `trans_words` SET `content_loc6`='May - for someone as exalted among the Zandalar as you are, I have something very special for you. Direct from our home in the South Seas... the Signets of the Zandalar! These signets are used to enhance any shoulder item you may possess. Should you seek might, mojo, or serenity - I have what you need!' WHERE `word_id` = 271928; -- locales_quest - details : entry=8246
UPDATE `trans_words` SET `content_loc6`='May fire burn within your heart, $N.$B$BThe elements await you.' WHERE `word_id` = 135696; -- locales_quest - offerrewardtext : entry=1464
