UPDATE `trans_words` SET `content_loc6`='May your aim be unerring, and the mark of your weapon be true, $R.$B$BAgamaggan\'s spirit is with you. Return <snort> to me if you wish. Mangletooth will bless you again if the great boar god deems me worthy. <snort>' WHERE `word_id` = 139692; -- locales_quest - offerrewardtext : entry=5043
UPDATE `trans_words` SET `content_loc6`='May your sense of honor and duty serve Azeroth well, $C.' WHERE `word_id` = 189264; -- locales_quest - offerrewardtext : entry=28790
